import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux';

// import TextField from '@material-ui/core/TextField';
// import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

import { get } from '../../actions/basic';


const useStyles = makeStyles({
  table: {
    minWidth: 650,
  },
});

function createData(college, group, wechat, status) {
  return { college, group, wechat, status };
}

const Dashboard = ({ wechats, get }) => {
  const [value, setValus] = useState([])

  const rows = [];
  useEffect(() => {
    get().then(function(data) {
      console.log(data)
      data.forEach(e => {
        rows.push(createData(e.college, e.group, e.wechat, e.status))
        console.log(rows)
      });
    })
    setValus(rows)
    console.log(value)
  }, [ wechats ])


  const classes = useStyles();

  return (
    <TableContainer component={Paper}>
      <Table className={classes.table} aria-label="simple table">
        <TableHead>
          <TableRow>
            <TableCell>id</TableCell>
            <TableCell align="right">学校</TableCell>
            <TableCell align="right">群</TableCell>
            <TableCell align="right">微信号</TableCell>
            <TableCell align="right">状态</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {value.map((row) => (
            <TableRow key={row.name}>
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.calories}</TableCell>
              <TableCell align="right">{row.fat}</TableCell>
              <TableCell align="right">{row.carbs}</TableCell>
              <TableCell align="right">{row.protein}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}


const mapStateToProps = state => ({
  wechats: state.basicWeChat
})

export default connect( mapStateToProps, { get } )(Dashboard)
