import React, { useEffect, useState, Fragment } from 'react'
import { connect } from 'react-redux';

import { makeStyles } from '@material-ui/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Box from '@material-ui/core/Box';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
  new: {
    marginTop: '25px',
    marginBottom: '25px'
  },
  newBox: {
    marginBottom: '25px'
  },
  newBoxForm: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    }
  },
  newBoxSubmit: {
    width: '30px',
    marginTop: '15px',
    backgroundColor: '#66CC00',
    color: 'white',
    '&:hover': {
      backgroundColor: 'green'
    }
  },
  table: {
    minWidth: 650,
  },
}));

// const newBoxProps = {
//   bgcolor: 'background.paper',
//   m: 1,
//   style: { width: '90%', height: '5rem' },
//   borderColor: 'text.primary',
// };

const BasicManage = () => {
  const classes = useStyles();

  return (
    <Fragment>
      <Button
        variant="contained"
        color="primary"
        className={classes.new}
      >
        添加校园大使
      </Button>
      <Box
        component="div"
        display="block"
        className={classes.newBox}
        // border={0.2}
        // {...newBoxProps}
      >
        <form className={classes.newBoxForm} noValidate autoComplete="off">
          <TextField
            id="outlined-basic"
            label="邮箱"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            label="姓名"
            variant="outlined"
          />
          <TextField
            id="outlined-basic"
            label="密码"
            variant="outlined"
          />
          <FormControl variant="outlined" className={classes.formControl}>
            <InputLabel id="demo-simple-select-outlined-label">Age</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              value={age}
              onChange={handleChange}
              label="Age"
            >
              <MenuItem value="">
                <em>None</em>
              </MenuItem>
              <MenuItem value={10}>Ten</MenuItem>
              <MenuItem value={20}>Twenty</MenuItem>
              <MenuItem value={30}>Thirty</MenuItem>
            </Select>
          </FormControl>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            className={classes.newBoxSubmit}
          >
            添加
          </Button>
        </form>
      </Box>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell align='center'>Data Id</TableCell>
              <TableCell align='center'>学校</TableCell>
              <TableCell align='center'>邮箱</TableCell>
              <TableCell align='center'>姓名</TableCell>
              <TableCell align='center'>状态</TableCell>
            </TableRow>
          </TableHead>
          {/* <TableBody>
            {value ? value.map((e) => (
              <TableRow key={e.id}>
                <TableCell component="th" scope="row" align='center'>
                  {e.id}
                </TableCell>
                <TableCell align='center'>{e.college}</TableCell>
                <TableCell align='center'>{e.group}</TableCell>
                <TableCell align='center'>{e.wechat}</TableCell>
                <TableCell align='center'>{e.status}</TableCell>
              </TableRow>
            )) : null}
          </TableBody> */}
        </Table>
      </TableContainer>
    </Fragment>
  )
}

export default BasicManage;
