import React, { useState } from 'react'
import { connect } from 'react-redux';

import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import { makeStyles } from '@material-ui/styles';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';


import { single } from '../../actions/basic';

const useStyles = makeStyles(theme => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: 200,
    },
    display: 'flex',
    alignContent: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  },
  container: {
    marginTop: '30px',
    marginBottom: '30px'
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  button: {
    // marginLeft: '25px',
    maxHeight: '35px',
    marginTop: '10px'
  }
}))

const Upload = ({ single }) => {
  const classes = useStyles()

  const [formData, setFormData] = useState({
    wechat: '',
    status: '',
  })

  const {wechat, status} = formData

  const onSubmit = e => {
    e.preventDefault()
    single(wechat, status)
  }


  const onChange = e => setFormData({
    ...formData,
    [e.target.name]: e.target.value
  })

  return (
    <div className={classes.container}>
      <form
        noValidate
        autoComplete="off"
        onSubmit={e => onSubmit(e)}
        className={classes.root}
      >
        <div>
          <TextField
              label="微信号"
              id="wechat"
              // defaultValue="Normal"
              // variant="outlined"
              onChange={e => onChange(e)}
          />
          <FormControl className={classes.formControl}>
            <InputLabel id="demo-simple-select-label">状态</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={status}
              onChange={e => onChange(e)}
            >
              <MenuItem value={'group'}>已拉进群</MenuItem>
              <MenuItem value={'transfer'}>转换中</MenuItem>
              <MenuItem value={'deal'}>已购买</MenuItem>
              <MenuItem value={'potential'}>潜在用户</MenuItem>
            </Select>
          </FormControl>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className={classes.button}
            type='submit'
          >
            查询
          </Button>
        </div>
      </form>
    </div>
  )
}

export default connect( null, {single} )(Upload)
